import React from 'react'
import {db,auth} from './Services/firebase'

class App extends React.Component{

  state ={
    fruits: null
  }

  componentDidMount(){

      db.collection('fruits')
        .get()
        .then(snapshot =>{
          const fruits = []
          snapshot.forEach( doc=>{
            const data = doc.data()
            fruits.push(data)

      })
      this.setState({fruits:fruits })})

      .catch( error => console.log(error))
  }



   handleSubmit=(e) =>{

    e.preventDefault()

    var name =document.getElementById('name').value
    var imageurl =document.getElementById('imageurl').value
    var description =document.getElementById('description').value

    // saveMessage(name, imageurl, description)
    db.collection('fruits')
      .add({
        Name:name,
        Imageurl:imageurl,
        Description:description
      })

  }




  render(){
    return(

     <div classname ="App" >

        <h1 > Please enter some data </h1>
        <form onSubmit = {this.handleSubmit}>
                <p>
                    <label>Name</label>
                    <input type="text" name="name" id="name" required />
                  </p>
                  <p>
                    <label>Imageurl</label>
                    <input type="url" name="imageurl" id="imageurl" />
                  </p>
                  <p>
                    <label>Description</label>
                    <textarea name="Description"  rows="3"  id="description" required></textarea>
                  </p>
                  <p class="full">
                    <button type="submit">Submit</button></p>


        </form>
                {
            this.state.fruits && this.state.fruits.map(fuckit=>{
              return(
                <div>
                  <p>{fruits.Name}</p>
                  <p>{fruits.Imageurl}</p>
                  <p>{fruits.Description}</p>
                </div>
              )

            })
        }
      </div>

    )
  }
}

export default App
