import React from 'react'
// import './App.css';
import {db,auth} from './Services/firebase'



class App extends React.Component{

  state ={
    fruits: null
  }

  componentDidMount(){

      db.collection('fruits')
      .get()
      .then(snapshot =>{
        const fruits = []
        snapshot.forEach( doc=>{
          const data = doc.data()
          fruits.push(data)

      })
      this.setState({fruits:fruits })})

      .catch( error => console.log(error))
  }


  addNewfruits =() =>{
    db.collection('fruits')
      .add({
        Name:"Banana",
        Imageurl:"www.google.com",
        Description:"This is Banana tile"
      })
  }

  render(){
    return(

     <div classname ="App" >

        <h1 > Started building the web app </h1>
        <p>
            <label>Name</label>
            <input type="text" name="name" id="name" required />
          </p>
          <p>
            <label>Imageurl</label>
            <input type="url" name="imageurl" id="imageurl" />
          </p>
          <p>
            <label>Description</label>
            <textarea name="Description"  rows="3"  id="description" required></textarea>
          </p>
          <p class="full">
            <button type="submit">Submit</button></p>
        <button onClick={this.addNewfruits}> Add a new Fruit </button>
        {
          this.state.fruits &&
          this.state.fruits.map(fruits=>{
            return(
              <div>
                <p>{fruits.Name}</p>
                <p>{fruits.Imageurl}</p>
                <p>{fruits.Description}</p>
              </div>
            )

            })
        }
      </div>

    )
  }
}

export default App
